package com.name.vanish;import org.bukkit.ChatColor;import org.bukkit.command.Command;import org.bukkit.command.CommandExecutor;import org.bukkit.command.CommandSender;import org.bukkit.entity.Player;import java.util.ArrayList;import java.util.List;public class CommandHandler implements CommandExecutor {    List<String> toggled = new ArrayList<String>();    public boolean onCommand(CommandSender sender, Command cmd, String label,                             String[] args) {        if (!(sender instanceof Player)) {            sender.sendMessage(ChatColor.BLUE + "PLAYER ONLY COMMAND!");            return false;        }        PlayerVanish pvInst = PlayerVanish.getInstance();        Player player = (Player) sender;        if (label.equalsIgnoreCase("pv")                || label.equalsIgnoreCase("playervanish")) {            if (args.length < 1) {                pvInst.SendCommandError(player);                return true;            }            switch (args[0].toLowerCase()) {                case "sinfo":                    if (player.hasPermission("pv.serverinfo")) {                        pvInst.sendMessage5(player);                    } else {                        pvInst.SendPermissionError(player);                    }                    break;                case "info":                    if (player.hasPermission("pv.info")) {                        pvInst.sendMessage4(player);                    } else {                        pvInst.SendPermissionError(player);                    }                    break;                case "about":                    pvInst.sendMessage(player);                    break;                case "hide":                    if (player.hasPermission("pv.hide")) {                        pvInst.hideAllPlayers(player);                    } else {                        pvInst.SendPermissionError(player);                    }                    break;                case "show":                    if (player.hasPermission("pv.show")) {                        pvInst.showAllPlayers(player);                    } else {                        pvInst.SendPermissionError(player);                    }                    break;                case "ip":                    if (player.hasPermission("pv.ip")) {                        pvInst.sendMessage2(player);                    } else {                        pvInst.SendPermissionError(player);                    }                    break;                case "help":                    pvInst.sendMessage3(player);                    break;                case "toggle":                    if (player.hasPermission("pv.toggle")) {                        pvInst.sendMessage7(player);                    } else {                        pvInst.SendPermissionError(player);                    }                    break;                case "fleave":                    if (player.hasPermission("pv.fleave")) {                        pvInst.sendMessage9(player);                    } else {                        pvInst.SendPermissionError(player);                    }                    break;                case "fjoin":                    if (player.hasPermission("pv.fjoin")) {                        pvInst.sendMessage10(player);                    } else {                        pvInst.SendPermissionError(player);                    }                    break;                default:                    pvInst.SendCommandError(player);                    break;            }            return true;        }        return false;    }}